#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int calc(char *s, int index)
{
    int o = 0, e = 0, len = 0;
    for (unsigned int i = index; i < strlen(s); i++)
    {
        if ((s[i] - '0') % 2 == 0)
            e += 1;
        else
            o += 1;
        if (o == e)
            len = o + e;
    }
    return (len);
}

char    *longest_subarray(char *arr)
{
    char *result;
    int *l;
    int len = strlen(arr);

    l = malloc(sizeof(int) * len);
    for (int i = 0; i < len; i++)
    {
        l[i] = 0;
    }
    
    int max = 0;
    for (int i = 0; i < len; i++)
    {
        int tmp = calc(arr, i);
        l[i] = tmp;
        if (tmp > max)
            max = tmp;
    }
    int i = 0;
    while (i < len)
    {
        if (l[i] == max)
        {
            break;
        }
        i++;
    }
    result = malloc(sizeof(char) * (max + 1));
    result[max] = '\0';
    for (int j = 0; j < max; j++)
        result[j] = arr[j + i];
    return (result);
}

int	main(void)
{
	char str1[] = "1357913579024680213579";
	printf("79135790246802\n%s\n", longest_subarray(str1));
	char str2[] = "134";
	printf("\n34\n%s\n", longest_subarray(str2));
	char str3[] = "454";
	printf("\n45\n%s\n", longest_subarray(str3));
	char str4[] = "2010102";
	printf("\n0101\n%s\n", longest_subarray(str4));
	char str5[] = "2";
	printf("\n[blank]\n%s\n", longest_subarray(str5));
	char str6[] = "";
	printf("\n[blank]\n%s\n", longest_subarray(str6));
	//Moulinette test 6
	char str7[] = "";
	printf("\n2253\n%s\n", longest_subarray(str7));
}