#include <stdlib.h>
#include <stdio.h>

int min(int a, int b)
{
	return ((a > b) ? b : a);
}

int ftstrlen(char *s)
{
	int i = 0;

	while (s[i])
	{
		i++;
	}
	return (i);
}

int moe(char *s)
{
	int i = 0;
	int o = 0;
	int e = 0;

	while (s[i])
	{
		if ((s[i] - '0') % 2 == 0)
			e++;
		else
			o++;
		i++;
	}
	return (min(e, o));
}

int bal(char *s, int len)
{
	int b = 0;
	int i = 0;

	while (i < len)
	{
		if ((s[i] - '0') % 2 == 0)
			b++;
		else
			b--;
		i++;
	}
	return (b);
}

char *ft_strndup(char *s, int len)
{
	int i = -1;
	char *p;

	p = (char *)malloc(sizeof(char) * (len + 1));
	p[len] = '\0';

	while (++i < len)
	{
		p[i] = s[i];
	}
	return (p);
}

char *longest_subarray(char *s)
{
	int i;
	int ml;
	int cl;

	ml = ftstrlen(s);
	cl = 2 * moe(s);
	i = 0;

	while (bal(&s[i], cl) != 0)
	{
		if (i + cl >= ml)
		{
			i = 0;
			cl--;
		}
		else
			i++;
	}
	return (ft_strndup(&s[i], cl));
}

